import React, { useState } from 'react';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import { useInView } from 'react-intersection-observer';

const FAQContainer = styled.section`
  padding: 8rem 2rem;
  background: linear-gradient(135deg, ${props => props.theme.colors.accent} 0%, ${props => props.theme.colors.white} 100%);
`;

const FAQContent = styled.div`
  max-width: 800px;
  margin: 0 auto;
`;

const SectionTitle = styled(motion.h2)`
  font-family: ${props => props.theme.fonts.primary};
  font-size: clamp(2.5rem, 5vw, 4rem);
  text-align: center;
  margin-bottom: 3rem;
  color: ${props => props.theme.colors.secondary};
`;

const FAQList = styled.div`
  display: flex;
  flex-direction: column;
  gap: 1rem;
`;

const FAQItem = styled(motion.div)`
  background: ${props => props.theme.colors.white};
  border-radius: 15px;
  overflow: hidden;
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
`;

const FAQQuestion = styled(motion.button)`
  width: 100%;
  padding: 1.5rem 2rem;
  background: none;
  border: none;
  text-align: left;
  font-size: 1.1rem;
  font-weight: 600;
  color: ${props => props.theme.colors.secondary};
  cursor: pointer;
  display: flex;
  justify-content: space-between;
  align-items: center;
  
  &:hover {
    background: ${props => props.theme.colors.accent};
  }
`;

const FAQIcon = styled(motion.span)`
  font-size: 1.5rem;
  color: ${props => props.theme.colors.primary};
`;

const FAQAnswer = styled(motion.div)`
  padding: 0 2rem 1.5rem;
  color: ${props => props.theme.colors.secondary};
  opacity: 0.8;
  line-height: 1.6;
`;

const FAQ = () => {
  const [openIndex, setOpenIndex] = useState(null);
  const [ref, inView] = useInView({ threshold: 0.3, triggerOnce: true });

  const faqs = [
    {
      question: "What materials do you use in your jewelry?",
      answer: "We use only the finest materials including 14k and 18k gold, platinum, sterling silver, and ethically sourced diamonds and gemstones. All our pieces come with certificates of authenticity."
    },
    {
      question: "Do you offer custom jewelry design?",
      answer: "Yes! We specialize in custom jewelry design. Our process includes consultation, 3D modeling, approval, and crafting. The timeline is typically 4-6 weeks depending on complexity."
    },
    {
      question: "What is your return and exchange policy?",
      answer: "We offer a 30-day return policy for unworn items in original condition. Custom pieces are final sale unless there's a manufacturing defect. All returns require original packaging and receipt."
    },
    {
      question: "Do you provide jewelry appraisals?",
      answer: "Yes, we provide certified appraisals for insurance, estate planning, or personal knowledge. Our certified gemologists ensure accurate valuations following industry standards."
    },
    {
      question: "How should I care for my jewelry?",
      answer: "Store pieces separately in soft pouches, avoid chemicals and perfumes, clean regularly with appropriate methods, and bring for professional cleaning annually. We provide detailed care instructions with each purchase."
    },
    {
      question: "Do you offer financing options?",
      answer: "Yes, we offer various financing options including 0% interest plans for qualified customers. We also accept major credit cards and offer layaway programs for special occasions."
    },
    {
      question: "How long does repair work take?",
      answer: "Simple repairs like ring sizing typically take 1-2 weeks. Complex restorations may take 3-4 weeks. We provide time estimates during consultation and keep you updated throughout the process."
    },
    {
      question: "Are your diamonds conflict-free?",
      answer: "Absolutely. We are committed to ethical sourcing and only work with suppliers who adhere to the Kimberley Process. All our diamonds are conflict-free and come with proper certification."
    }
  ];

  const toggleFAQ = (index) => {
    setOpenIndex(openIndex === index ? null : index);
  };

  return (
    <FAQContainer ref={ref}>
      <FAQContent>
        <SectionTitle
          initial={{ opacity: 0, y: 50 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8 }}
        >
          Frequently Asked Questions
        </SectionTitle>

        <FAQList>
          {faqs.map((faq, index) => (
            <FAQItem
              key={index}
              initial={{ opacity: 0, y: 30 }}
              animate={inView ? { opacity: 1, y: 0 } : {}}
              transition={{ duration: 0.6, delay: index * 0.1 }}
            >
              <FAQQuestion
                onClick={() => toggleFAQ(index)}
                whileHover={{ backgroundColor: '#f8f8f8' }}
              >
                {faq.question}
                <FAQIcon
                  animate={{ rotate: openIndex === index ? 45 : 0 }}
                  transition={{ duration: 0.3 }}
                >
                  +
                </FAQIcon>
              </FAQQuestion>
              
              <AnimatePresence>
                {openIndex === index && (
                  <FAQAnswer
                    initial={{ height: 0, opacity: 0 }}
                    animate={{ height: 'auto', opacity: 1 }}
                    exit={{ height: 0, opacity: 0 }}
                    transition={{ duration: 0.3 }}
                  >
                    {faq.answer}
                  </FAQAnswer>
                )}
              </AnimatePresence>
            </FAQItem>
          ))}
        </FAQList>
      </FAQContent>
    </FAQContainer>
  );
};

export default FAQ;
