import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router } from 'react-router-dom';
import { ThemeProvider } from 'styled-components';
import GlobalStyles from './styles/GlobalStyles';
import { theme } from './styles/theme';
// import useCustomCursor from './hooks/useCustomCursor'; // HAPUS INI
import LoadingScreen from './components/LoadingScreen';
import ThreeJSBackground from './components/ThreeJSBackground';
import Header from './components/Header';
import Hero from './components/Hero';
import ParallaxSection from './components/ParallaxSection';
import ProductShowcase from './components/ProductShowcase';
import Services from './components/Services';
import About from './components/About';
import Testimonials from './components/Testimonials';
import FAQ from './components/FAQ';
import InstagramFeed from './components/InstagramFeed';
import Newsletter from './components/Newsletter';
import Contact from './components/Contact';
import Footer from './components/Footer';
import ScrollToTop from './components/ScrollToTop';
import BackToTop from './components/BackToTop';
// import MouseFollower from './components/MouseFollower'; // HAPUS INI
import CookieConsent from './components/CookieConsent';

function App() {
  const [isLoading, setIsLoading] = useState(true);
  // useCustomCursor(); // HAPUS INI

  const handleLoadingComplete = () => {
    setIsLoading(false);
  };

  useEffect(() => {
    // Smooth scrolling setup
    let lenis;
    
    const initLenis = async () => {
      try {
        const Lenis = (await import('lenis')).default;
        lenis = new Lenis({
          duration: 1.2,
          easing: (t) => Math.min(1, 1.001 - Math.pow(2, -10 * t)),
          direction: 'vertical',
          gestureDirection: 'vertical',
          smooth: true,
          mouseMultiplier: 1,
          smoothTouch: false,
          touchMultiplier: 2,
          infinite: false,
        });
        
        function raf(time) {
          lenis.raf(time);
          requestAnimationFrame(raf);
        }
        
        requestAnimationFrame(raf);
      } catch (error) {
        console.log('Lenis not available, using default scrolling');
      }
    };

    if (!isLoading) {
      initLenis();
    }

    // Preload critical images
    const preloadImages = [
      'https://images.unsplash.com/photo-1515562141207-7a88fb7ce338?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80',
      'https://images.unsplash.com/photo-1605100804763-247f67b3557e?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80',
      'https://images.unsplash.com/photo-1573408301185-9146fe634ad0?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80'
    ];

    preloadImages.forEach(src => {
      const img = new Image();
      img.src = src;
    });

    return () => {
      if (lenis) {
        lenis.destroy();
      }
    };
  }, [isLoading]);

  return (
    <ThemeProvider theme={theme}>
      <Router>
        <GlobalStyles />
        {isLoading ? (
          <LoadingScreen onLoadingComplete={handleLoadingComplete} />
        ) : (
          <>
            <ThreeJSBackground />
            {/* <MouseFollower /> HAPUS INI */}
            <Header />
            <Hero />
            <ParallaxSection />
            <ProductShowcase />
            <Services />
            <About />
            <Testimonials />
            <FAQ />
            <InstagramFeed />
            <Newsletter />
            <Contact />
            <Footer />
            <ScrollToTop />
            <BackToTop />
            <CookieConsent />
          </>
        )}
      </Router>
    </ThemeProvider>
  );
}

export default App;
