import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import { ThemeProvider } from 'styled-components';
import { AnimatePresence } from 'framer-motion';
import GlobalStyles from './styles/GlobalStyles';
import { theme } from './styles/theme';
import useCustomCursor from './hooks/useCustomCursor';
import LoadingScreen from './components/LoadingScreen';
import Header from './components/Header';
import Footer from './components/Footer';
import ScrollToTop from './components/ScrollToTop';
import BackToTop from './components/BackToTop';
import MouseFollower from './components/MouseFollower';
import CookieConsent from './components/CookieConsent';

// Import pages
import HomePage from './pages/Home';
import Products from './pages/Products';
import ServicesPage from './pages/Services';
import AboutPage from './pages/About';
import ContactPage from './pages/Contact';

const AnimatedRoutes = ({ isLoadingComplete }) => {
  const location = useLocation();

  const AnimatedRoutes = () => {
  const location = useLocation();

  // Homepage Component
  const HomePage = () => (
    <PageTransition pageName="LUXE JEWELRY">
      <Hero />
      <ParallaxSection />
      <FeaturedCollection />
      <Testimonials />
      <FAQ />
      <InstagramFeed />
      <Newsletter />
    </PageTransition>
  );

  const ProductsPage = () => (
    <PageTransition pageName="LUXE JEWELRY">
      <Products />
    </PageTransition>
  );
  const ServicesPage = () => (
    <PageTransition pageName="LUXE JEWELRY">
      <Services />
    </PageTransition>
  );
  const AboutPage = () => (
    <PageTransition pageName="LUXE JEWELRY">
      <About />
    </PageTransition>
  );
  const ContactPage = () => (
    <PageTransition pageName="LUXE JEWELRY">
      <Contact />
    </PageTransition>
  );

  return (
    <AnimatePresence mode="wait">
      <Routes location={location} key={location.pathname}>
        <Route path="/" element={<HomePage />} />
        <Route path="/Products" element={<ProductsPage />} />
        <Route path="/Services" element={<ServicesPage />} />
        <Route path="/About" element={<AboutPage />} />
        <Route path="/Contact" element={<ContactPage />} />
      </Routes>
    </AnimatePresence>
  );
};
  
  return (
    <AnimatePresence mode="wait">
      <Routes location={location} key={location.pathname}>
        <Route path="/" element={<HomePage isLoadingComplete={isLoadingComplete} />} />
        <Route path="/products" element={<Products />} />
        <Route path="/services" element={<ServicesPage />} />
        <Route path="/about" element={<AboutPage />} />
        <Route path="/contact" element={<ContactPage />} />
      </Routes>
    </AnimatePresence>
  );
};

function App() {
  const [isLoading, setIsLoading] = useState(true);
  const [isLoadingComplete, setIsLoadingComplete] = useState(false);
  useCustomCursor();

  const handleLoadingComplete = () => {
    setIsLoading(false);
    // Set flag bahwa loading sudah complete
    setTimeout(() => {
      setIsLoadingComplete(true);
    }, 100);
  };

  useEffect(() => {
    // Smooth scrolling setup
    let lenis;
    
    const initLenis = async () => {
      try {
        const Lenis = (await import('lenis')).default;
        lenis = new Lenis({
          duration: 1.2,
          easing: (t) => Math.min(1, 1.001 - Math.pow(2, -10 * t)),
          direction: 'vertical',
          gestureDirection: 'vertical',
          smooth: true,
          mouseMultiplier: 1,
          smoothTouch: false,
          touchMultiplier: 2,
          infinite: false,
        });
        
        function raf(time) {
          lenis.raf(time);
          requestAnimationFrame(raf);
        }
        
        requestAnimationFrame(raf);
      } catch (error) {
        console.log('Lenis not available, using default scrolling');
      }
    };

    // Hanya init lenis setelah loading complete dan ada delay
    if (!isLoading && isLoadingComplete) {
      setTimeout(() => {
        initLenis();
      }, 500); // Delay untuk menghindari konflik
    }

    // Preload critical images
    const preloadImages = [
      'https://images.unsplash.com/photo-1515562141207-7a88fb7ce338?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80',
      'https://images.unsplash.com/photo-1605100804763-247f67b3557e?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80',
      'https://images.unsplash.com/photo-1573408301185-9146fe634ad0?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80'
    ];

    preloadImages.forEach(src => {
      const img = new Image();
      img.src = src;
    });

    return () => {
      if (lenis) {
        lenis.destroy();
      }
    };
  }, [isLoading, isLoadingComplete]);

  return (
    <ThemeProvider theme={theme}>
      <Router>
        <GlobalStyles />
        {isLoading ? (
          <LoadingScreen onLoadingComplete={handleLoadingComplete} />
        ) : (
          <>
            <MouseFollower />
            <Header />
            <AnimatedRoutes isLoadingComplete={isLoadingComplete} />
            <Footer />
            <ScrollToTop />
            <BackToTop />
            <CookieConsent />
          </>
        )}
      </Router>
    </ThemeProvider>
  );
}

export default App;
