import React, { useState, useEffect, useCallback } from 'react';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';

const TestimonialsSection = styled.section`
  padding: 100px 0;
  background: linear-gradient(135deg, #1a1a1a 0%, #2d2d2d 100%);
  position: relative;
  overflow: hidden;
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 2rem;
  text-align: center;
`;

const Title = styled(motion.h2)`
  font-family: 'Playfair Display', serif;
  font-size: 3rem;
  color: #D4AF37;
  margin-bottom: 3rem;
  text-align: center;

  @media (max-width: 768px) {
    font-size: 2rem;
  }
`;

const TestimonialCard = styled(motion.div)`
  background: rgba(255, 255, 255, 0.05);
  backdrop-filter: blur(10px);
  border-radius: 20px;
  padding: 3rem;
  margin: 0 auto;
  max-width: 800px;
  border: 1px solid rgba(212, 175, 55, 0.3);
`;

const TestimonialText = styled.p`
  font-size: 1.2rem;
  line-height: 1.8;
  color: white;
  margin-bottom: 2rem;
  font-style: italic;
`;

const TestimonialAuthor = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 1rem;
`;

const AuthorImage = styled.div`
  width: 60px;
  height: 60px;
  border-radius: 50%;
  background: linear-gradient(45deg, #D4AF37, #F4E4BC);
  display: flex;
  align-items: center;
  justify-content: center;
  font-weight: bold;
  color: #1a1a1a;
`;

const AuthorInfo = styled.div`
  text-align: left;
`;

const AuthorName = styled.h4`
  color: #D4AF37;
  margin: 0;
  font-size: 1.1rem;
`;

const AuthorTitle = styled.p`
  color: #ccc;
  margin: 0;
  font-size: 0.9rem;
`;

const NavigationDots = styled.div`
  display: flex;
  justify-content: center;
  gap: 1rem;
  margin-top: 2rem;
`;

const Dot = styled.button`
  width: 12px;
  height: 12px;
  border-radius: 50%;
  border: none;
  background: ${props => props.active ? '#D4AF37' : 'rgba(255, 255, 255, 0.3)'};
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    background: #D4AF37;
  }
`;

const Testimonials = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const testimonials = [
    {
      text: "The craftsmanship is absolutely stunning. Every piece tells a story of elegance and sophistication. I've never received so many compliments on my jewelry!",
      author: "Sarah Johnson",
      title: "Fashion Designer",
      initial: "SJ"
    },
    {
      text: "Luxe Jewelry exceeded all my expectations. The attention to detail and quality is unmatched. It's not just jewelry, it's wearable art.",
      author: "Michael Chen",
      title: "Art Collector",
      initial: "MC"
    },
    {
      text: "I bought my engagement ring here and it was perfect. The staff was knowledgeable and helped me find exactly what I was looking for.",
      author: "Emily Rodriguez",
      title: "Bride-to-be",
      initial: "ER"
    }
  ];

  const nextTestimonial = useCallback(() => {
    setCurrentIndex((prev) => (prev + 1) % testimonials.length);
  }, [testimonials.length]);

  useEffect(() => {
    const interval = setInterval(nextTestimonial, 5000);
    return () => clearInterval(interval);
  }, [nextTestimonial]);

  return (
    <TestimonialsSection id="testimonials">
      <Container>
        <Title
          initial={{ opacity: 0, y: 50 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
        >
          What Our Clients Say
        </Title>

        <AnimatePresence mode="wait">
          <TestimonialCard
            key={currentIndex}
            initial={{ opacity: 0, x: 100 }}
            animate={{ opacity: 1, x: 0 }}
            exit={{ opacity: 0, x: -100 }}
            transition={{ duration: 0.5 }}
          >
            <TestimonialText>
              "{testimonials[currentIndex].text}"
            </TestimonialText>
            
            <TestimonialAuthor>
              <AuthorImage>
                {testimonials[currentIndex].initial}
              </AuthorImage>
              <AuthorInfo>
                <AuthorName>{testimonials[currentIndex].author}</AuthorName>
                <AuthorTitle>{testimonials[currentIndex].title}</AuthorTitle>
              </AuthorInfo>
            </TestimonialAuthor>
          </TestimonialCard>
        </AnimatePresence>

        <NavigationDots>
          {testimonials.map((_, index) => (
            <Dot
              key={index}
              active={index === currentIndex}
              onClick={() => setCurrentIndex(index)}
            />
          ))}
        </NavigationDots>
      </Container>
    </TestimonialsSection>
  );
};

export default Testimonials;
