import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';

const FeaturedContainer = styled.section`
  padding: 120px 0;
  background: ${props => props.theme.colors.gradient.background};
  position: relative;
  overflow: hidden;
`;

const Container = styled.div`
  max-width: 1400px;
  margin: 0 auto;
  padding: 0 20px;
`;

const SectionHeader = styled.div`
  text-align: center;
  margin-bottom: 80px;
`;

const Title = styled(motion.h2)`
  font-size: clamp(3rem, 6vw, 4rem);
  margin-bottom: 20px;
  font-weight: 100;
  letter-spacing: 6px;
  font-family: ${props => props.theme.fonts.primary};
  background: ${props => props.theme.colors.gradient.elegantGold};
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  position: relative;
  
  &::after {
    content: '';
    position: absolute;
    bottom: -15px;
    left: 50%;
    transform: translateX(-50%);
    width: 80px;
    height: 2px;
    background: ${props => props.theme.colors.gradient.goldSilver};
  }
  
  @media (max-width: 768px) {
    letter-spacing: 3px;
  }
`;

const Subtitle = styled(motion.p)`
  font-size: 1.2rem;
  color: rgba(255, 255, 255, 0.8);
  font-weight: 300;
  letter-spacing: 1px;
  max-width: 600px;
  margin: 0 auto 40px;
  line-height: 1.8;
`;

const ViewAllButton = styled(motion(Link))`
  display: inline-block;
  padding: 15px 35px;
  background: transparent;
  border: 2px solid transparent;
  background-image: ${props => props.theme.colors.gradient.goldSilver};
  background-origin: border-box;
  background-clip: content-box, border-box;
  color: ${props => props.theme.colors.gold};
  text-decoration: none;
  font-weight: 400;
  letter-spacing: 2px;
  transition: all 0.4s ease;
  position: relative;
  overflow: hidden;
  
  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: ${props => props.theme.colors.gradient.goldSilver};
    transition: left 0.5s ease;
    z-index: -1;
  }
  
  &:hover::before {
    left: 0;
  }
  
  &:hover {
    color: #000;
    transform: translateY(-2px);
    box-shadow: 0 10px 25px rgba(212, 175, 55, 0.3);
  }
`;

const ProductGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 40px;
  margin-bottom: 60px;
  
  @media (max-width: 768px) {
    grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
    gap: 30px;
  }
`;

const ProductCard = styled(motion.div)`
  background: rgba(255, 255, 255, 0.02);
  backdrop-filter: blur(20px);
  border: 1px solid rgba(212, 175, 55, 0.2);
  overflow: hidden;
  transition: all 0.4s ease;
  
  &:hover {
    transform: translateY(-10px);
    border: 2px solid transparent;
    background-image: linear-gradient(rgba(255, 255, 255, 0.05), rgba(255, 255, 255, 0.05)), 
                      ${props => props.theme.colors.gradient.goldSilver};
    background-origin: border-box;
    background-clip: content-box, border-box;
    box-shadow: 0 20px 40px rgba(212, 175, 55, 0.2);
  }
`;

const ProductImage = styled.div`
  height: 300px;
  background-image: url(${props => props.src});
  background-size: cover;
  background-position: center;
  position: relative;
  overflow: hidden;
  
  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(45deg, rgba(212, 175, 55, 0.1), rgba(192, 192, 192, 0.1));
    opacity: 0;
    transition: opacity 0.3s ease;
  }
  
  ${ProductCard}:hover &::before {
    opacity: 1;
  }
`;

const ProductInfo = styled.div`
  padding: 30px;
  text-align: center;
`;

const ProductName = styled.h3`
  color: #fff;
  font-size: 1.3rem;
  margin-bottom: 10px;
  font-weight: 300;
  letter-spacing: 1px;
  font-family: ${props => props.theme.fonts.primary};
`;

const ProductPrice = styled.p`
  background: ${props => props.theme.colors.gradient.goldSilver};
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  font-size: 1.2rem;
  font-weight: 500;
  letter-spacing: 1px;
`;

const BackgroundElements = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  pointer-events: none;
  overflow: hidden;
`;

const BackgroundShape = styled(motion.div)`
  position: absolute;
  background: ${props => props.theme.colors.gradient.goldSilver};
  opacity: 0.03;
  border-radius: 50%;
`;

const FeaturedCollection = () => {
  // Produk pilihan untuk homepage (hanya 6 items)
  const featuredProducts = [
    {
      id: 1,
      name: 'Diamond Eternity Ring',
      price: '$2,850',
      image: 'https://images.unsplash.com/photo-1515562141207-7a88fb7ce338?ixlib=rb-4.0.3&auto=format&fit=crop&w=600&q=80'
    },
    {
      id: 2,
      name: 'Pearl Elegance Necklace',
      price: '$1,650',
      image: 'https://images.unsplash.com/photo-1605100804763-247f67b3557e?ixlib=rb-4.0.3&auto=format&fit=crop&w=600&q=80'
    },
    {
      id: 3,
      name: 'Sapphire Drop Earrings',
      price: '$3,200',
      image: 'https://images.unsplash.com/photo-1573408301185-9146fe634ad0?ixlib=rb-4.0.3&auto=format&fit=crop&w=600&q=80'
    },
    {
      id: 4,
      name: 'Gold Tennis Bracelet',
      price: '$4,100',
      image: 'https://images.unsplash.com/photo-1611591437281-460bfbe1220a?ixlib=rb-4.0.3&auto=format&fit=crop&w=600&q=80'
    },
    {
      id: 5,
      name: 'Emerald Vintage Ring',
      price: '$5,500',
      image: 'https://images.unsplash.com/photo-1584302179602-e4819bb92daa?ixlib=rb-4.0.3&auto=format&fit=crop&w=600&q=80'
    },
    {
      id: 6,
      name: 'Diamond Stud Earrings',
      price: '$2,300',
      image: 'https://images.unsplash.com/photo-1506630448388-4e683c67ddb0?ixlib=rb-4.0.3&auto=format&fit=crop&w=600&q=80'
    }
  ];

  return (
    <FeaturedContainer>
      <BackgroundElements>
        <BackgroundShape
          style={{ width: '200px', height: '200px', top: '10%', left: '5%' }}
          animate={{
            y: [0, -50, 0],
            x: [0, 30, 0],
            rotate: [0, 180, 360],
          }}
          transition={{
            duration: 15,
            repeat: Infinity,
            ease: "easeInOut"
          }}
        />
        <BackgroundShape
          style={{ width: '150px', height: '150px', top: '60%', right: '10%' }}
          animate={{
            y: [0, 40, 0],
            x: [0, -20, 0],
          }}
          transition={{
            duration: 12,
            repeat: Infinity,
            ease: "easeInOut"
          }}
        />
        <BackgroundShape
          style={{ width: '100px', height: '100px', bottom: '20%', left: '15%' }}
          animate={{
            y: [0, -30, 0],
            rotate: [0, -180, -360],
          }}
          transition={{
            duration: 10,
            repeat: Infinity,
            ease: "easeInOut"
          }}
        />
      </BackgroundElements>

      <Container>
        <SectionHeader>
          <Title
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 1 }}
            viewport={{ once: true }}
          >
            FEATURED COLLECTION
          </Title>
          
          <Subtitle
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.2 }}
            viewport={{ once: true }}
          >
            Discover our handpicked selection of exquisite jewelry pieces, 
            crafted with precision and designed for timeless elegance.
          </Subtitle>
          
          <ViewAllButton
            to="/products"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.4 }}
            viewport={{ once: true }}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            VIEW ALL COLLECTION
          </ViewAllButton>
        </SectionHeader>
        
        <ProductGrid>
          {featuredProducts.map((product, index) => (
            <ProductCard
              key={product.id}
              initial={{ opacity: 0, y: 60 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: index * 0.1 }}
              viewport={{ once: true }}
              whileHover={{ scale: 1.02 }}
            >
              <ProductImage src={product.image} />
              <ProductInfo>
                <ProductName>{product.name}</ProductName>
                <ProductPrice>{product.price}</ProductPrice>
              </ProductInfo>
            </ProductCard>
          ))}
        </ProductGrid>
      </Container>
    </FeaturedContainer>
  );
};

export default FeaturedCollection;
