import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const HeaderContainer = styled(motion.header)`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  z-index: 1000;
  padding: 20px 0;
  background: ${props => props.scrolled ? 'rgba(0, 0, 0, 0.95)' : 'transparent'};
  backdrop-filter: ${props => props.scrolled ? 'blur(20px)' : 'none'};
  border-bottom: ${props => props.scrolled ? '1px solid rgba(212, 175, 55, 0.2)' : 'none'};
  transition: all 0.3s ease;
`;

const Container = styled.div`
  max-width: 1400px;
  margin: 0 auto;
  padding: 0 20px;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const Logo = styled(Link)`
  font-size: 2.2rem;
  font-weight: 300;
  background: ${props => props.theme.colors.gradient.goldSilver};
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  text-decoration: none;
  letter-spacing: 4px;
  font-family: ${props => props.theme.fonts.primary};
  transition: all 0.3s ease;
  
  &:hover {
    transform: scale(1.05);
    filter: brightness(1.2);
  }
`;

const Nav = styled.nav`
  display: flex;
  gap: 40px;
  
  @media (max-width: 768px) {
    display: ${props => props.isOpen ? 'flex' : 'none'};
    position: absolute;
    top: 100%;
    left: 0;
    right: 0;
    background: rgba(0, 0, 0, 0.95);
    backdrop-filter: blur(20px);
    flex-direction: column;
    padding: 30px 20px;
    border-top: 1px solid rgba(212, 175, 55, 0.2);
  }
`;

const NavLink = styled(Link)`
  color: ${props => props.active ? props.theme.colors.gold : 'rgba(255, 255, 255, 0.8)'};
  text-decoration: none;
  font-weight: 300;
  letter-spacing: 1px;
  font-size: 1rem;
  transition: all 0.3s ease;
  position: relative;
  
  &::after {
    content: '';
    position: absolute;
    bottom: -5px;
    left: 0;
    width: ${props => props.active ? '100%' : '0'};
    height: 1px;
    background: ${props => props.theme.colors.gradient.goldSilver};
    transition: width 0.3s ease;
  }
  
  &:hover {
    background: ${props => props.theme.colors.gradient.goldSilver};
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    
    &::after {
      width: 100%;
    }
  }
`;

const MobileMenuButton = styled.button`
  display: none;
  background: none;
  border: none;
  background: ${props => props.theme.colors.gradient.goldSilver};
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  font-size: 1.5rem;
  cursor: pointer;
  
  @media (max-width: 768px) {
    display: block;
  }
`;

const Header = () => {
  const [scrolled, setScrolled] = useState(false);
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const location = useLocation();

  // Function untuk handle navigation
  const handleNavClick = (e, targetId) => {
    e.preventDefault();
    
    // Jika sudah di halaman home dan klik link yang sama, jangan scroll
    if (location.pathname === '/' && window.location.hash === `#${targetId}`) {
      return; // Exit early, no scroll
    }
    
    // Jika di halaman lain, navigate ke home dulu
    if (location.pathname !== '/') {
      window.location.href = `/#${targetId}`;
      return;
    }
    
    // Jika di home tapi section berbeda, scroll ke section
    const element = document.getElementById(targetId);
    if (element) {
      element.scrollIntoView({ 
        behavior: 'smooth',
        block: 'start'
      });
      
      // Update URL hash
      window.history.pushState(null, null, `#${targetId}`);
    }
    
    // Close mobile menu
    setIsMobileMenuOpen(false);
  };

  // Handle home link
  const handleHomeClick = (e) => {
    e.preventDefault();
    
    // Jika sudah di home dan di top, jangan scroll
    if (location.pathname === '/' && window.scrollY < 100) {
      return;
    }
    
    // Jika di halaman lain, navigate ke home
    if (location.pathname !== '/') {
      window.location.href = '/';
      return;
    }
    
    // Jika di home tapi tidak di top, scroll ke top
    window.scrollTo({ top: 0, behavior: 'smooth' });
    window.history.pushState(null, null, '/');
    setIsMobileMenuOpen(false);
  };

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const navItems = [
    { name: 'Home', path: '/' },
    { name: 'Products', path: '/products' },
    { name: 'Services', path: '/services' },
    { name: 'About', path: '/about' },
    { name: 'Contact', path: '/contact' }
  ];

 return (
    <HeaderContainer isScrolled={isScrolled}>
      <HeaderContent>
        <Logo 
          onClick={handleHomeClick}
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
        >
          LUXE
        </Logo>

        <Navigation>
          {navItems.map((item) => (
            <NavLink
              key={item.name}
              href={item.isHome ? '/' : `#${item.id}`}
              onClick={(e) => item.isHome ? handleHomeClick(e) : handleNavClick(e, item.id)}
              active={
                item.isHome 
                  ? location.pathname === '/' && window.location.hash === ''
                  : window.location.hash === `#${item.id}`
              }
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              {item.name}
            </NavLink>
          ))}
        </Navigation>

        <MobileMenuButton
          onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
          isOpen={isMobileMenuOpen}
        >
          <span></span>
          <span></span>
          <span></span>
        </MobileMenuButton>
      </HeaderContent>

      <MobileMenu isOpen={isMobileMenuOpen}>
        {navItems.map((item) => (
          <MobileNavLink
            key={item.name}
            href={item.isHome ? '/' : `#${item.id}`}
            onClick={(e) => item.isHome ? handleHomeClick(e) : handleNavClick(e, item.id)}
          >
            {item.name}
          </MobileNavLink>
        ))}
      </MobileMenu>
    </HeaderContainer>
  );
};

export default Header;
