export const theme = {
  colors: {
    primary: '#C9A96E', // Warm gold
    secondary: '#1a1a1a', // Dark background
    gold: '#D4AF37', // Classic gold
    silver: '#C0C0C0', // Silver
    lightGold: '#F4E4BC', // Light champagne
    darkGold: '#B8860B', // Dark gold
    white: '#FFFFFF',
    black: '#000000',
    gradient: {
      goldSilver: 'linear-gradient(135deg, #D4AF37 0%, #C0C0C0 50%, #D4AF37 100%)',
      darkGold: 'linear-gradient(135deg, #C9A96E 0%, #D4AF37 100%)',
      elegantGold: 'linear-gradient(45deg, #F4E4BC 0%, #D4AF37 50%, #B8860B 100%)',
      silverGold: 'linear-gradient(135deg, #C0C0C0 0%, #D4AF37 100%)',
      background: 'linear-gradient(135deg, #0a0a0a 0%, #1a1a1a 50%, #2a2a2a 100%)'
    }
  },
  fonts: {
    primary: "'Playfair Display', serif",
    secondary: "'Inter', sans-serif"
  },
  breakpoints: {
    mobile: '768px',
    tablet: '1024px',
    desktop: '1200px'
  }
};
