import React, { useState } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const ShowcaseContainer = styled.section`
  padding: 100px 0;
  background: rgba(0, 0, 0, 0.8);
  backdrop-filter: blur(10px);
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
`;

const Title = styled(motion.h2)`
  text-align: center;
  font-size: 3rem;
  color: #ffd700;
  margin-bottom: 60px;
  font-weight: 300;
  letter-spacing: 2px;
`;

const ProductGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 30px;
  margin-bottom: 60px;
`;

const ProductCard = styled(motion.div)`
  background: rgba(255, 255, 255, 0.05);
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 215, 0, 0.2);
  overflow: hidden;
  cursor: pointer;
  transition: all 0.3s ease;
  
  /* Rounded top corners only */
  border-radius: 20px 20px 0 0;
  
  &:hover {
    transform: translateY(-10px);
    border-color: rgba(255, 215, 0, 0.5);
    box-shadow: 0 20px 40px rgba(255, 215, 0, 0.1);
  }
`;

const ProductImage = styled.div`
  width: 100%;
  height: 250px;
  background-image: url(${props => props.src});
  background-size: cover;
  background-position: center;
  position: relative;
  
  &::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(
      45deg,
      transparent 0%,
      rgba(255, 215, 0, 0.1) 50%,
      transparent 100%
    );
    opacity: 0;
    transition: opacity 0.3s ease;
  }
  
  ${ProductCard}:hover &::after {
    opacity: 1;
  }
`;

const ProductInfo = styled.div`
  padding: 25px;
`;

const ProductName = styled.h3`
  color: #fff;
  font-size: 1.4rem;
  margin-bottom: 10px;
  font-weight: 400;
`;

const ProductPrice = styled.p`
  color: #ffd700;
  font-size: 1.2rem;
  font-weight: 600;
  margin-bottom: 15px;
`;

const ProductDescription = styled.p`
  color: rgba(255, 255, 255, 0.8);
  font-size: 0.9rem;
  line-height: 1.6;
`;

const ViewAllButton = styled(motion.button)`
  display: block;
  margin: 0 auto;
  padding: 15px 40px;
  background: linear-gradient(45deg, #ffd700, #ffed4e);
  color: #000;
  border: none;
  border-radius: 30px;
  font-size: 1.1rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  
  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 10px 20px rgba(255, 215, 0, 0.3);
  }
`;

const products = [
  {
    id: 1,
    name: "Diamond Elegance Ring",
    price: "$2,499",
    image: "https://images.unsplash.com/photo-1605100804763-247f67b3557e?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80",
    description: "Stunning diamond ring with 18k gold band"
  },
  {
    id: 2,
    name: "Pearl Necklace Set",
    price: "$1,899",
    image: "https://images.unsplash.com/photo-1573408301185-9146fe634ad0?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80",
    description: "Classic pearl necklace with matching earrings"
  },
  {
    id: 3,
    name: "Gold Bracelet",
    price: "$1,299",
    image: "https://images.unsplash.com/photo-1611591437281-460bfbe1220a?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80",
    description: "Handcrafted 22k gold bracelet with intricate design"
  },
  {
    id: 4,
    name: "Emerald Earrings",
    price: "$3,199",
    image: "https://images.unsplash.com/photo-1617038260897-41a1f14a8ca0?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80",
    description: "Exquisite emerald earrings with diamond accents"
  },
  {
    id: 5,
    name: "Sapphire Pendant",
    price: "$2,799",
    image: "https://images.unsplash.com/photo-1515562141207-7a88fb7ce338?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80",
    description: "Royal blue sapphire pendant with white gold chain"
  },
  {
    id: 6,
    name: "Wedding Ring Set",
    price: "$4,999",
    image: "https://images.unsplash.com/photo-1544376664-80b17f09d399?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80",
    description: "Perfect matching wedding rings for couples"
  }
];

const ProductShowcase = () => {
  const [selectedProduct, setSelectedProduct] = useState(null);

  return (
    <ShowcaseContainer id="products">
      <Container>
        <Title
          initial={{ opacity: 0, y: 50 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
        >
          Featured Collection
        </Title>
        
        <ProductGrid>
          {products.map((product, index) => (
            <ProductCard
              key={product.id}
              initial={{ opacity: 0, y: 50 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: index * 0.1 }}
              viewport={{ once: true }}
              onClick={() => setSelectedProduct(product)}
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
            >
              <ProductImage src={product.image} />
              <ProductInfo>
                <ProductName>{product.name}</ProductName>
                <ProductPrice>{product.price}</ProductPrice>
                <ProductDescription>{product.description}</ProductDescription>
              </ProductInfo>
            </ProductCard>
          ))}
        </ProductGrid>
        
        <ViewAllButton
          initial={{ opacity: 0 }}
          whileInView={{ opacity: 1 }}
          transition={{ duration: 0.8, delay: 0.5 }}
          viewport={{ once: true }}
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
        >
          View All Products
        </ViewAllButton>
      </Container>
    </ShowcaseContainer>
  );
};

export default ProductShowcase;
