import React, { useState } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const ShowcaseContainer = styled.section`
  padding: 120px 0;
  background: linear-gradient(135deg, #0a0a0a 0%, #1a1a1a 100%);
  position: relative;
  
  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(
      90deg,
      transparent,
      rgba(255, 215, 0, 0.03),
      transparent
    );
  }
`;

const Container = styled.div`
  max-width: 1400px;
  margin: 0 auto;
  padding: 0 20px;
`;

const SectionHeader = styled.div`
  text-align: center;
  margin-bottom: 100px;
`;

const Title = styled(motion.h2)`
  font-size: clamp(3rem, 6vw, 5rem);
  color: #ffffff;
  margin-bottom: 20px;
  font-weight: 100;
  letter-spacing: 8px;
  font-family: 'Playfair Display', serif;
  position: relative;
  
  &::after {
    content: '';
    position: absolute;
    bottom: -15px;
    left: 50%;
    transform: translateX(-50%);
    width: 80px;
    height: 1px;
    background: linear-gradient(90deg, transparent, #ffd700, transparent);
  }
`;

const Subtitle = styled(motion.p)`
  font-size: 1.2rem;
  color: rgba(255, 255, 255, 0.7);
  font-weight: 300;
  letter-spacing: 2px;
  max-width: 600px;
  margin: 0 auto;
  line-height: 1.8;
`;

const ProductGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
  gap: 40px;
  margin-bottom: 80px;
`;

const ProductCard = styled(motion.div)`
  background: rgba(255, 255, 255, 0.02);
  backdrop-filter: blur(20px);
  border: 1px solid rgba(255, 215, 0, 0.1);
  overflow: hidden;
  cursor: pointer;
  transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
  
  /* Rounded top corners only - seperti yang diminta */
  border-radius: 25px 25px 0 0;
  
  &:hover {
    transform: translateY(-15px);
    border-color: rgba(255, 215, 0, 0.3);
    box-shadow: 0 30px 60px rgba(0, 0, 0, 0.3);
    background: rgba(255, 255, 255, 0.05);
  }
`;

const ProductImage = styled.div`
  width: 100%;
  height: 300px;
  background-image: url(${props => props.src});
  background-size: cover;
  background-position: center;
  position: relative;
  
  &::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(
      45deg,
      transparent 0%,
      rgba(255, 215, 0, 0.1) 50%,
      transparent 100%
    );
    opacity: 0;
    transition: opacity 0.4s ease;
  }
  
  ${ProductCard}:hover &::after {
    opacity: 1;
  }
`;

const ProductInfo = styled.div`
  padding: 35px;
`;

const ProductName = styled.h3`
  color: #fff;
  font-size: 1.5rem;
  margin-bottom: 12px;
  font-weight: 300;
  letter-spacing: 1px;
  font-family: 'Playfair Display', serif;
`;

const ProductPrice = styled.p`
  color: #ffd700;
  font-size: 1.3rem;
  font-weight: 400;
  margin-bottom: 15px;
  letter-spacing: 1px;
`;

const ProductDescription = styled.p`
  color: rgba(255, 255, 255, 0.7);
  font-size: 1rem;
  line-height: 1.7;
  font-weight: 300;
  letter-spacing: 0.5px;
`;

const ViewAllButton = styled(motion.button)`
  display: block;
  margin: 0 auto;
  padding: 20px 50px;
  background: linear-gradient(45deg, rgba(255, 215, 0, 0.1), rgba(255, 215, 0, 0.05));
  color: #ffd700;
  border: 2px solid #ffd700;
  font-size: 1.1rem;
  font-weight: 300;
  letter-spacing: 3px;
  cursor: pointer;
  transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
  backdrop-filter: blur(15px);
  position: relative;
  overflow: hidden;
  
  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255, 215, 0, 0.2), transparent);
    transition: left 0.6s ease;
  }
  
  &:hover::before {
    left: 100%;
  }
  
  &:hover {
    background: rgba(255, 215, 0, 0.9);
    color: #000;
    transform: translateY(-3px);
    box-shadow: 0 15px 35px rgba(255, 215, 0, 0.4);
  }
`;

const products = [
  {
    id: 1,
    name: "Diamond Elegance Ring",
    price: "$2,499",
    image: "https://images.unsplash.com/photo-1605100804763-247f67b3557e?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80",
    description: "Stunning diamond ring with 18k gold band, crafted for eternal elegance"
  },
  {
    id: 2,
    name: "Pearl Necklace Set",
    price: "$1,899",
    image: "https://images.unsplash.com/photo-1573408301185-9146fe634ad0?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80",
    description: "Classic pearl necklace with matching earrings, timeless sophistication"
  },
  {
    id: 3,
    name: "Gold Bracelet",
    price: "$1,299",
    image: "https://images.unsplash.com/photo-1611591437281-460bfbe1220a?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80",
    description: "Handcrafted 22k gold bracelet with intricate design details"
  },
  {
    id: 4,
    name: "Emerald Earrings",
    price: "$3,199",
    image: "https://images.unsplash.com/photo-1617038260897-41a1f14a8ca0?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80",
    description: "Exquisite emerald earrings with diamond accents, luxury redefined"
  },
  {
    id: 5,
    name: "Sapphire Pendant",
    price: "$2,799",
    image: "https://images.unsplash.com/photo-1515562141207-7a88fb7ce338?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80",
    description: "Royal blue sapphire pendant with white gold chain"
  },
  {
    id: 6,
    name: "Wedding Ring Set",
    price: "$4,999",
    image: "https://images.unsplash.com/photo-1544376664-80b17f09d399?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80",
    description: "Perfect matching wedding rings for couples, symbol of eternal love"
  }
];

const ProductShowcase = () => {
  return (
    <ShowcaseContainer id="products">
      <Container>
        <SectionHeader>
          <Title
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 1 }}
            viewport={{ once: true }}
          >
            FEATURED COLLECTION
          </Title>
          <Subtitle
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.2 }}
            viewport={{ once: true }}
          >
            Handpicked pieces that embody luxury, craftsmanship, and timeless beauty
          </Subtitle>
        </SectionHeader>
        
        <ProductGrid>
          {products.map((product, index) => (
            <ProductCard
              key={product.id}
              initial={{ opacity: 0, y: 60 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: index * 0.1 }}
              viewport={{ once: true }}
              whileHover={{ scale: 1.02 }}
            >
              <ProductImage src={product.image} />
              <ProductInfo>
                <ProductName>{product.name}</ProductName>
                <ProductPrice>{product.price}</ProductPrice>
                <ProductDescription>{product.description}</ProductDescription>
              </ProductInfo>
            </ProductCard>
          ))}
        </ProductGrid>
        
        <ViewAllButton
          initial={{ opacity: 0 }}
          whileInView={{ opacity: 1 }}
          transition={{ duration: 0.8, delay: 0.5 }}
          viewport={{ once: true }}
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
        >
          VIEW ALL COLLECTION
        </ViewAllButton>
      </Container>
    </ShowcaseContainer>
  );
};

export default ProductShowcase;
