import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import styled from 'styled-components';
import { motion } from 'framer-motion';

// ... styled components tetap sama

const Header = () => {
  const [scrolled, setScrolled] = useState(false);
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const location = useLocation();

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 50);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Function untuk handle navigation dengan check current page
  const handleNavigation = (e, path, targetId = null) => {
    e.preventDefault();
    
    // Jika targetId ada (untuk home sections)
    if (targetId) {
      // Jika di halaman lain, redirect ke home dengan hash
      if (location.pathname !== '/') {
        window.location.href = `/#${targetId}`;
        return;
      }
      
      // Check jika sudah di section yang sama
      const currentHash = window.location.hash.replace('#', '');
      if (currentHash === targetId) {
        console.log('Already at this section');
        setMobileMenuOpen(false);
        return;
      }
      
      // Smooth scroll ke section
      const element = document.getElementById(targetId);
      if (element) {
        element.scrollIntoView({ 
          behavior: 'smooth',
          block: 'start'
        });
        window.history.pushState(null, null, `/#${targetId}`);
      }
      
      setMobileMenuOpen(false);
      return;
    }
    
    // Untuk halaman biasa (Products, Services, About, Contact)
    // Jika sudah di halaman yang sama, jangan navigate
    if (location.pathname === path) {
      console.log('Already at this page');
      setMobileMenuOpen(false);
      return;
    }
    
    // Navigate ke halaman baru
    window.location.href = path;
    setMobileMenuOpen(false);
  };

  // Handle home click
  const handleHomeClick = (e) => {
    e.preventDefault();
    
    // Jika di halaman lain, navigate ke home
    if (location.pathname !== '/') {
      window.location.href = '/';
      setMobileMenuOpen(false);
      return;
    }
    
    // Jika sudah di home dan di top, jangan scroll
    if (window.scrollY < 100 && !window.location.hash) {
      console.log('Already at home top');
      setMobileMenuOpen(false);
      return;
    }
    
    // Scroll ke top
    window.scrollTo({ top: 0, behavior: 'smooth' });
    window.history.pushState(null, null, '/');
    setMobileMenuOpen(false);
  };

  const navItems = [
    { name: 'Home', path: '/', isHome: true },
    { name: 'Products', path: '/products' },
    { name: 'Services', path: '/services', targetId: 'services' }, // Bisa halaman atau section
    { name: 'About', path: '/about', targetId: 'about' },
    { name: 'Contact', path: '/contact', targetId: 'contact' }
  ];

  return (
    <HeaderContainer
      scrolled={scrolled}
      initial={{ y: -100 }}
      animate={{ y: 0 }}
      transition={{ duration: 0.8 }}
    >
      <Container>
        <Logo onClick={handleHomeClick}>LUXE</Logo>
        
        <Nav isOpen={mobileMenuOpen}>
          {navItems.map((item) => {
            if (item.isHome) {
              return (
                <NavLink
                  key={item.name}
                  href="/"
                  onClick={handleHomeClick}
                  active={location.pathname === '/' && !window.location.hash}
                >
                  {item.name}
                </NavLink>
              );
            }
            
            return (
              <NavLink
                key={item.name}
                href={item.path}
                onClick={(e) => handleNavigation(e, item.path, item.targetId)}
                active={
                  location.pathname === item.path || 
                  (location.pathname === '/' && window.location.hash === `#${item.targetId}`)
                }
              >
                {item.name}
              </NavLink>
            );
          })}
        </Nav>
        
        <MobileMenuButton
          onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
        >
          ☰
        </MobileMenuButton>
      </Container>
    </HeaderContainer>
  );
};

export default Header;
